mapper.select:
SELECT 

FROM user a

WHERE a.id = #{id}

SELECT 

FROM user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.wechat_id = #{wechatId}


		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')




		ORDER BY ${page.orderBy}

SELECT 

FROM user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE user SET 	
	wechat_id = #{wechatId},
	user_name = #{userName},
	password = #{password},
	user_type = #{userType}
WHERE id = #{id}

UPDATE user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
