mapper.select:
SELECT 

FROM book a

WHERE a.book_id = #{id}

SELECT
FROM book a
a.is_status = 1
AND a.book_isbn = #{bookIsbn}
AND a.book_name LIKE '%'||#{bookName}||'%' '%'+#{bookName}+'%' concat('%',#{bookName},'%')
AND a.book_author LIKE '%'||#{bookAuthor}||'%' '%'+#{bookAuthor}+'%' concat('%',#{bookAuthor},'%')
AND b.publisher_id = #{publisherId}
ORDER BY ${page.orderBy}

mapper.update:
UPDATE  book SET
is_status = 0


		WHERE book_id = #{id}


		WHERE book_publisherid = #{publisherId}

mapper.select:
SELECT

FROM book a

