(	SELECT b_c.book_id FROM book_category AS b_c

		b_c.category_id = #{categoryId}

)

SELECT count(*) FROM book a WHERE a.book_id IN ( SELECT b_c.book_id FROM book_category AS b_c
b_c.category_id = #{categoryId}
)
