mapper.select:
SELECT 

FROM book a

WHERE a.id = #{id}

SELECT 

FROM book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.book_isbn = #{bookIsbn}


		AND a.book_name LIKE 
			'%'||#{bookName}||'%'
			'%'+#{bookName}+'%'
			concat('%',#{bookName},'%')


		AND a.book_author = #{bookAuthor}




		ORDER BY ${page.orderBy}

SELECT 

FROM book a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
