mapper.select:
SELECT 

FROM book_publisher a

WHERE a.id = #{id}

SELECT
FROM book_publisher a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.publisher_name LIKE '%'||#{publisherName}||'%' '%'+#{publisherName}+'%' concat('%',#{publisherName},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM book_publisher a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.insert:
INSERT INTO book_publisher(
	publisher_id,
	publisher_name,
	del_flag
) VALUES (
	#{publisherId},
	#{publisherName},
	#{delFlag}
)

mapper.update:
UPDATE book_publisher SET 	
	publisher_name = #{publisherName}
WHERE id = #{id}

UPDATE book_publisher SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
