mapper.select:
SELECT 

FROM user_info a

WHERE a.id = #{id}

SELECT 

FROM user_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.openid = #{openid}


		AND a.person_name LIKE 
			'%'||#{personName}||'%'
			'%'+#{personName}+'%'
			concat('%',#{personName},'%')


		AND a.idcard = #{idCard}


		AND a.phonenumber = #{phoneNumber}


		AND a.email = #{email}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM user_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE user_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
